import React, { Component } from "react";
import PostData from "./bio.json";
class App extends Component {
  constructor() {
    super();
    this.state = {
      questionNum: 0,
      isEnd: false,
    };
  }

  answerLetters = ["a", "b", "c", "d", "e"];

  prevQuestion = () => {
    this.setState({
      questionNum: this.state.questionNum - 1,
    });
  };

  nextQuestion = () => {
    if (this.state.questionNum + 1 < PostData.length) {
      this.setState({
        questionNum: this.state.questionNum + 1,
      });
    } else {
      this.setState({
        isEnd: true,
      });
    }
  };

  handleOption = (e) => {
    console.log(e.option);
  };

  render() {
    return (
      <>
        <center>
          {this.state.isEnd ? (
            <p>End of the quiz!</p>
          ) : (
            <>
              <div>
                {/* {PostData.map((postDetail, index) => {
              return <p>{postDetail.title}</p>;
            })}  */}
                <p>
                  {this.state.questionNum + 1}.&nbsp;
                  {PostData[this.state.questionNum].title}
                </p>
                <p>
                  {PostData[this.state.questionNum].choices.map(
                    (option, index) => {
                      return (
                        <span
                          data={option}
                          onClick={(e) => this.handleOption(e)}
                        >
                          {this.answerLetters[index]}. {option}
                          <br />
                          <br />
                        </span>
                      );
                    }
                  )}
                </p>
              </div>
              {this.state.questionNum != 0 ? (
                <button onClick={(e) => this.prevQuestion()}>Back</button>
              ) : (
                <></>
              )}
              &nbsp;&nbsp;
              <button onClick={(e) => this.nextQuestion()}>Next</button>
            </>
          )}
        </center>
      </>
    );
  }
}

export default App;
