import React, { Component } from "react";
import PostData from "./bio.json";
import "./style.css";
class App extends Component {
  constructor() {
    super();
    this.state = {
      questionNum: 0,
      isEnd: false,
      chosenAnswer: "",
      answersArray: [],
    };
  }

  answerLetters = ["a", "b", "c", "d", "e"];

  setAnswer(event) {
    console.log(event.target.value);
    this.setState({
      chosenAnswer: event.target.value,
      checkStatus: true,
    });
  }

  nextQuestion = () => {
    this.setState({
      answersArray: [...this.state.answersArray, this.state.chosenAnswer],
    });
    if (this.state.questionNum + 1 < PostData.length) {
      this.setState({
        questionNum: this.state.questionNum + 1,
      });
    } else {
      this.setState({
        isEnd: true,
      });
    }
  };

  render() {
    return (
      <>
        <center>
          <h1>AP Biology</h1>
          {this.state.isEnd ? (
            <>
              <p>End of the quiz!</p>
              {this.state.answersArray.map((answer, index) => {
                if (answer == PostData[index].correct) {
                  return <p>{index + 1}. Correct!</p>;
                } else {
                  return <p>{index + 1}. Incorrect!</p>;
                }
              })}
            </>
          ) : (
            <>
              <div className="questionBox">
                {/* {PostData.map((postDetail, index) => {
              return <p>{postDetail.title}</p>;
            })}  */}
                <p className="questionTitleInner" id="questionTitle">
                  {this.state.questionNum + 1}.&nbsp;
                  {PostData[this.state.questionNum].title}
                </p>
                <hr></hr>
                <p>
                  {PostData[this.state.questionNum].choices.map(
                    (option, index) => {
                      return (
                        <>
                          <label>
                            <div
                              className="questionBoxInner"
                              style={{
                                backgroundColor:
                                  this.state.chosenAnswer == option
                                    ? "pink"
                                    : "",
                                padding: "15px",
                              }}
                              for={option}
                            >
                              {/* {this.answerLetters[index]}. */}
                              <label
                                className="optionText"
                                style={{ display: "flex" }}
                              >
                                <input
                                  style={{
                                    marginRight: "12px",
                                    flexShrink: "0",
                                  }}
                                  onClick={this.setAnswer.bind(this)}
                                  type="radio"
                                  id={option}
                                  value={option}
                                  name="options"
                                  checked={this.state.chosenAnswer == option}
                                />
                                {option}
                              </label>
                            </div>
                          </label>
                        </>
                      );
                    }
                  )}
                </p>
              </div>
              <span
                id="nextButton"
                style={{ position: "relative", top: "20px" }}
                onClick={(e) => this.nextQuestion()}
              >
                NEXT
              </span>
            </>
          )}
        </center>
      </>
    );
  }
}

export default App;
