import React, { Component } from "react";
import PostData from "./bio.json";
import "./style.css";
class App extends Component {
  constructor() {
    super();
    this.state = {
      questionNum: 0,
      isEnd: false,
      chosenAnswer: "",
      answersArray: [],
    };
  }

  answerLetters = ["a", "b", "c", "d", "e"];

  setAnswer(event) {
    console.log(event.target.value);
    this.setState({
      chosenAnswer: event.target.value,
      checkStatus: true,
    });
  }

  nextQuestion = () => {
    this.setState({
      answersArray: [...this.state.answersArray, this.state.chosenAnswer],
    });
    if (this.state.questionNum + 1 < PostData.length) {
      this.setState({
        questionNum: this.state.questionNum + 1,
      });
    } else {
      this.setState({
        isEnd: true,
      });
    }
  };

  render() {
    return (
      <>
        <center>
          <h1>AP Biology</h1>
          {this.state.isEnd ? (
            <>
              <p>End of the quiz!</p>
              {this.state.answersArray.map((answer, index) => {
                if (answer == PostData[index].correct) {
                  return <p>{index + 1}. Correct!</p>;
                } else {
                  return <p>{index + 1}. Incorrect!</p>;
                }
              })}
            </>
          ) : (
            <>
              <div className="questionBox">
                {/* {PostData.map((postDetail, index) => {
              return <p>{postDetail.title}</p>;
            })}  */}
                <p className="questionBoxInner">
                  {this.state.questionNum + 1}.&nbsp;
                  {PostData[this.state.questionNum].title}
                </p>
                <span style={{ marginTop: "0.2rem" }}>
                  <p>
                    {PostData[this.state.questionNum].choices.map(
                      (option, index) => {
                        return (
                          <div
                            className="questionBoxInner"
                            style={{
                              backgroundColor:
                                this.state.chosenAnswer == option
                                  ? "yellow"
                                  : "",
                            }}
                            value={option}
                          >
                            {/* {this.answerLetters[index]}. */}
                            <label style={{ display: "flex" }} for={option}>
                              <input
                                style={{ marginRight: "12px", flexShrink: "0" }}
                                onChange={this.setAnswer.bind(this)}
                                type="radio"
                                id={option}
                                value={option}
                                name="options"
                                checked={this.state.chosenAnswer == option}
                              />
                              {option}
                            </label>
                            <br />
                          </div>
                        );
                      }
                    )}
                    <span onClick={(e) => this.nextQuestion()}>NEXT</span>
                  </p>
                </span>
              </div>
            </>
          )}
        </center>
      </>
    );
  }
}

export default App;
