import React, { Component } from "react";
import PostData from "./bio.json";
class App extends Component {
  constructor() {
    super();
    this.state = {
      questionNum: 0,
      message: "",
    };
  }

  nextQuestion = () => {
    if (this.state.questionNum + 1 < PostData.length) {
      this.setState({
        questionNum: this.state.questionNum + 1,
      });
    } else {
      this.setState({
        message: "End of the quiz",
      });
    }
  };

  render() {
    return (
      <>
        <center>
          <p>{this.state.message}</p>
          <div>
            {/* {PostData.map((postDetail, index) => {
              return <p>{postDetail.title}</p>;
            })}  */}
            <p>{PostData[this.state.questionNum].title}</p>
            <p>
              {PostData[this.state.questionNum].choices.map((choice, index) => {
                return <p>{choice}</p>;
              })}
            </p>
          </div>
          <button onClick={(e) => this.nextQuestion()}>Next</button>
        </center>
      </>
    );
  }
}

export default App;
