import React, { useEffect, useState } from "react";
import firebaseApp from "./firebaseapp";
import firebase from "firebase";

export default function Login() {
  var currentUser = "";

  const [user, setUser] = useState(localStorage.getItem("user"));
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [hasAccount, setHasAccount] = useState(false);

  const clearInputs = () => {
    setEmail("");
    setPassword("");
  };

  const clearErrors = () => {
    setEmailError("");
  };

  const handleLogin = () => {
    clearErrors();
    // clearInputs();
    firebaseApp
      .auth()
      .signInWithEmailAndPassword(email, password)
      .catch((err) => {
        switch (err.code) {
          case "auth/invalid-email":
          case "auth/user-disabled":
          case "auth/user-not-found":
            setEmailError("Email not found");
            break;
          case "auth/wrong-password":
            setPasswordError("Wrong password");
            break;
        }
      });
  };

  const handleSignup = () => {
    clearErrors();
    // clearInputs();
    console.log(username);
    firebaseApp
      .auth()
      .createUserWithEmailAndPassword(email, password)
      .then(function (data) {
        console.log("uid", data.user.uid);
        firebase
          .database()
          .ref("users/" + data.user.uid)
          .set({
            id: data.user.uid,
            username: username,
            email: email,
          });
      })
      .catch((err) => {
        console.log("error occured");
        switch (err.code) {
          case "auth/email-already-in-use":
          case "auth/invalid-email":
            setEmailError("Invalid email");
            break;
          case "auth/weak-password":
            setPasswordError("Weak password");
            break;
        }
      });
  };

  const handleLogout = () => {
    firebaseApp.auth().signOut();
    localStorage.clear();
    localStorage.setItem("user", "");
  };

  const authListener = () => {
    firebaseApp.auth().onAuthStateChanged((user) => {
      if (user) {
        clearInputs();
        setUser(user);
        localStorage.setItem("user", user);
        console.log(username);
        console.log(user.uid);
      } else {
        setUser("");
      }
    });
  };

  useEffect(() => {
    authListener();
  }, []);

  if (user) {
    return (
      <>
        <center>
          <p>Logged In</p>
          <button onClick={handleLogout}>Log Out</button>
        </center>
      </>
    );
  } else {
    return (
      <>
        <>
          <center>
            <br />

            <div className="dashboard">
              <input
                type="text"
                name="name"
                placeholder="Email"
                className="loginBox"
                style={{
                  outline: "currentcolor none medium",
                }}
                autoComplete="off"
                onChange={(e) => setEmail(e.target.value)}
              />
              <br />
              <input
                type="text"
                name="name"
                placeholder="Password"
                className="loginBox"
                style={{
                  outline: "currentcolor none medium",
                  marginTop: "20px",
                }}
                autoComplete="off"
                onChange={(e) => setPassword(e.target.value)}
              />
              <br />
              <button
                className="loginBox"
                style={{ marginTop: "20px", cursor: "pointer" }}
                onClick={handleLogin}
              >
                Submit
              </button>
              <br /> <br />
              <br />
              <p
                className="questionTitleInner"
                style={{ marginBottom: "10px", fontFamily: "Source Sans Pro" }}
              >
                Login is only available for registered organizations. Through
                logging in, any student-run organization may post and share
                opportunities to be visible on the front page of this site. If
                you are part of an organization and wish to register, please
                fill out this Google Form to apply. All organizations must go
                through this application to be registered and gain login access
                (and posting ability) on the site. After completing the form,
                please allow up to 3 business days to get your login credentials
                (will be emailed).
              </p>
            </div>
          </center>
        </>
      </>
    );
  }
}
