import React, { Component } from "react";
import PostData from "./bio.json";
import "./style.css";
class App extends Component {
  constructor() {
    super();
    this.state = {
      questionNum: 0,
      isEnd: false,
      chosenAnswer: "",
      answersArray: [],
      score: 0,
    };
  }

  answerLetters = ["a", "b", "c", "d", "e"];

  setAnswer(event) {
    console.log(event.target.value);
    this.setState({
      chosenAnswer: event.target.value,
      checkStatus: true,
    });
  }

  nextQuestion = () => {
    this.setState({
      answersArray: [...this.state.answersArray, this.state.chosenAnswer],
      chosenAnswer: "",
    });
    if (this.state.questionNum + 1 < PostData.length) {
      this.setState({
        questionNum: this.state.questionNum + 1,
      });
    } else {
      this.setState({
        isEnd: true,
      });
    }
  };

  render() {
    return (
      <>
        <center>
          <h1>AP Biology (MC)</h1>
          {this.state.isEnd ? (
            <>
              <p>Your Score: {}</p>
              {this.state.answersArray.map((answer, index) => {
                if (answer === PostData[index].correct) {
                  this.setState({
                    score: this.state.score + 1,
                  });
                  return <p key={index}>{index + 1}. Correct!</p>;
                } else {
                  return <p key={index}>{index + 1}. Incorrect!</p>;
                }
              })}
            </>
          ) : (
            <>
              <div className="questionBox">
                {/* {PostData.map((postDetail, index) => {
              return <p>{postDetail.title}</p>;
            })}  */}
                <p className="questionTitleInner" id="questionTitle">
                  {this.state.questionNum + 1}.&nbsp;
                  {PostData[this.state.questionNum].title}
                </p>
                <hr></hr>

                {PostData[this.state.questionNum].choices.map(
                  (option, index) => {
                    return (
                      <div key={index}>
                        <label>
                          <div
                            className="questionBoxInner"
                            style={{
                              backgroundColor:
                                this.state.chosenAnswer ===
                                this.answerLetters[index]
                                  ? "pink"
                                  : "",
                              padding: "15px",
                            }}
                            htmlFor={this.answerLetters[index]}
                          >
                            {/* {this.answerLetters[index]}. */}
                            <label
                              className="optionText"
                              style={{ display: "flex" }}
                            >
                              <input
                                style={{
                                  marginRight: "12px",
                                  flexShrink: "0",
                                }}
                                onChange={this.setAnswer.bind(this)}
                                type="radio"
                                id={this.answerLetters[index]}
                                value={this.answerLetters[index]}
                                name="options"
                                checked={
                                  this.state.chosenAnswer ===
                                  this.answerLetters[index]
                                }
                              />
                              {option}
                            </label>
                          </div>
                        </label>
                      </div>
                    );
                  }
                )}
                <p></p>
              </div>
              <span
                id="nextButton"
                style={{ position: "relative", top: "20px" }}
                onClick={(e) => this.nextQuestion()}
              >
                NEXT
              </span>
            </>
          )}
        </center>
      </>
    );
  }
}

export default App;
