import React from "react";
import "./style.css";
import firebase from "firebase";
import firebaseApp from "./firebaseapp";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useHistory,
} from "react-router-dom";

export default class Blog extends React.Component {
  constructor(props) {
    super(props);
    this.setEmail = this.setEmail.bind(this);
    this.setPassword = this.setPassword.bind(this);
    this.state = {
      email: "",
      password: "",
      user: "",
      emailError: "",
      passwordError: "",
    };
  }

  clearInputs = () => {
    this.setState({
      email: "",
      password: "",
    });
  };

  componentWillMount() {
    /* 
    this.setState({
      user: localStorage.getItem("user"),
    }); */
    firebase.auth().onAuthStateChanged(function (user) {
      this.setState({
        user: user,
      });
    });
  }

  authListener = () => {
    firebaseApp.auth().onAuthStateChanged((user) => {
      if (user) {
        this.clearInputs();
        this.setState({
          user: user,
        });

        localStorage.setItem("user", user);

        console.log(user.uid);
      } else {
        this.setState({
          user: "",
        });
      }
    });
  };

  goHome(e) {
    console.log("hello");
    this.props.history.push("/");
  }

  setEmail(e) {
    this.setState({
      email: e.target.value,
    });
  }
  setPassword(e) {
    this.setState({
      password: e.target.value,
    });
  }

  clearErrors() {
    this.setState({
      emailError: "",
    });
  }

  handleLogin(e) {
    console.log("hello");
  }

  handleLogin = () => {
    this.clearErrors();
    // clearInputs();
    firebaseApp
      .auth()
      .signInWithEmailAndPassword(this.state.email, this.state.password)
      .catch((err) => {
        console.log("stephanie");
        switch (err.code) {
          case "auth/invalid-email":
          case "auth/user-disabled":
          case "auth/user-not-found":
            this.setState({
              emailError: "Email not found",
            });
            break;
          case "auth/wrong-password":
            this.setState({
              passwordError: "Wrong Password",
            });
            break;
        }
      });
  };

  handleLogout = () => {
    firebaseApp.auth().signOut();
    localStorage.clear();
    localStorage.setItem("user", "");
  };

  render() {
    firebase.auth().onAuthStateChanged(function (user) {
      if (user) {
        console.log("logged in!");
        console.log(user);
      } else {
        console.log(user);
      }
    });

    if (localStorage.user) {
      return (
        <>
          <p>Logged In</p>
          <button onClick={this.handleLogout}>Log out</button>
        </>
      );
    } else {
      return (
        <>
          {this.state.user}
          <center>
            <br />

            <div className="dashboard">
              <input
                type="text"
                name="name"
                placeholder="Email"
                className="loginBox"
                style={{
                  outline: "currentcolor none medium",
                }}
                autoComplete="off"
                onChange={this.setEmail}
              />
              <br />
              <input
                type="text"
                name="name"
                placeholder="Password"
                className="loginBox"
                style={{
                  outline: "currentcolor none medium",
                  marginTop: "20px",
                }}
                autoComplete="off"
                onChange={this.setPassword}
              />
              <br />
              <button
                className="loginBox"
                style={{ marginTop: "20px", cursor: "pointer" }}
                onClick={this.handleLogin}
              >
                Submit
              </button>
              <br /> <br />
              <br />
              <p
                className="questionTitleInner"
                style={{ marginBottom: "10px", fontFamily: "Source Sans Pro" }}
              >
                Login is only available for registered organizations. Through
                logging in, any student-run organization may post and share
                opportunities to be visible on the front page of this site. If
                you are part of an organization and wish to register, please
                fill out this Google Form to apply. All organizations must go
                through this application to be registered and gain login access
                (and posting ability) on the site. After completing the form,
                please allow up to 3 business days to get your login credentials
                (will be emailed).
              </p>
              {this.state.user}
            </div>
          </center>
        </>
      );
    }
  }
}
