export default class Blog extends React.Component {
  constructor(props) {
    super(props);
    this.state = { terms: "" };
    this.handleClick = this.handleClick.bind(this);
  }

  componentWillMount() {
    fetch(one)
      .then((response) => response.text())
      .then((text) => {
        console.log(text);
        this.setState({ terms: text });
      });
  }

