import React, { Component } from "react";
import PostData from "./bio.json";
class App extends Component {
  constructor() {
    super();
    this.state = {
      questionNum: 0,
      isEnd: false,
    };
  }

  answerLetters = ["a", "b", "c", "d", "e"];

  nextQuestion = () => {
    if (this.state.questionNum + 1 < PostData.length) {
      this.setState({
        questionNum: this.state.questionNum + 1,
      });
    } else {
      this.setState({
        isEnd: true,
      });
    }
  };

  render() {
    return (
      <>
        <center>
          {this.state.isEnd ? (
            <p>End of the quiz!</p>
          ) : (
            <>
              <div>
                {/* {PostData.map((postDetail, index) => {
              return <p>{postDetail.title}</p>;
            })}  */}
                <p>{PostData[this.state.questionNum].title}</p>
                <p>
                  {PostData[this.state.questionNum].choices.map(
                    (choice, index) => {
                      return (
                        <p>
                          {this.answerLetters[index]}. {choice}
                        </p>
                      );
                    }
                  )}
                </p>
              </div>
              <button onClick={(e) => this.nextQuestion()}>Back</button>
              <button onClick={(e) => this.nextQuestion()}>Next</button>
            </>
          )}
        </center>
      </>
    );
  }
}

export default App;
